
(function(){tinymce.PluginManager.requireLangPack('Archiv');tinymce.create('tinymce.plugins.ArchivPlugin',{init:function(ed,url){ed.addCommand('mceArchiv_files',function(){ed.windowManager.open({file:url+'/dialog.htm',width:800+parseInt(ed.getLang('mceArchiv_files.delta_width',0)),height:600+parseInt(ed.getLang('mceArchiv_files.delta_height',0)),inline:1},{plugin_url:url,browserType:'files'});});ed.addCommand('mceArchiv_images',function(){ed.windowManager.open({file:url+'/dialog.htm',width:800+parseInt(ed.getLang('mceArchiv_images.delta_width',0)),height:600+parseInt(ed.getLang('mceArchiv_images.delta_height',0)),inline:1},{plugin_url:url,browserType:'images'});});ed.addButton('Archiv_files',{title:'Archiv.Fdesc',cmd:'mceArchiv_files',image:url+'/img/insertfile.gif'});ed.addButton('Archiv_images',{title:'Archiv.Idesc',cmd:'mceArchiv_images',image:url+'/img/insertimage.gif'});},createControl:function(n,cm){return null;},getInfo:function(){return{longname:'Archiv plugin',author:'Wouter van Kuipers',authorurl:'http://www.pwnd.nl',infourl:'http://archiv.pwnd.nl',version:"1.0"};}});tinymce.PluginManager.add('Archiv',tinymce.plugins.ArchivPlugin);})();